"use strict";
var livePublicKey = $("#live_public_key"),
    testPublicKey = $("#test_public_key"),
    btnCopyPublicTest = $("#btn_copy_test_public_key"),
    btnCopyPublicLive = $("#btn_copy_live_public_key"),
    isRtl = "rtl" === $("html").attr("data-textdirection");
btnCopyPublicLive.on("click", function () {
    livePublicKey.select(), document.execCommand("copy"), toastr.success("", "Copied to clipboard!", { rtl: isRtl });
});
btnCopyPublicTest.on("click", function () {
    testPublicKey.select(), document.execCommand("copy"), toastr.success("", "Copied to clipboard!", { rtl: isRtl });
});
